<template>
  <div class="cc-loading" :style="{ flexDirection: vertical ? 'column' : 'row' }">
    <div
      class="cc-loading"
      :class="[`${className}`]"
      :style="{
        width: size + 'px',
        height: size + 'px', 
        borderLeftColor: type ==='circle' && color ? color : '#c7c7c7',
        
      }"
    ></div>
    <div
      :style="{ color: textColor, fontSize: textSize + 'px' }"
      :class="{ 'cc-loading-icon-left': !vertical, 'cc-loading-icon-top': vertical }"
    >
      <slot></slot>
    </div>
  </div>
</template>

<script setup lang="ts">
import { computed, defineProps, PropType } from 'vue'

let props = defineProps({
  type: {
    type: String as PropType<'circle' | 'spinner'>,
    default: 'circle'
  },
  // 自定义颜色
  color: {
    type: String,
    default: '#c8c9cc'
  },
  // 尺寸大小
  size: {
    type: String,
    default: '22'
  },
  // 文字大小
  textSize: {
    type: String,
    default: '14'
  },
  // 文字颜色
  textColor: {
    type: String,
    default: '#c9c9c9'
  },
  // 是否垂直排列图标和文字内容
  vertical: {
    type: Boolean,
    default: false
  }
})

let className = computed(() => {
  if (props.type) return `cc-loading-icon-${props.type}`
  else return ''
})
</script>

<style lang="scss">
.cc-loading {
  display: flex;
  &-icon {
    &-circle {
      animation: loading 1s linear infinite;
      width: 11px;
      height: 11px;
      border-radius: 50%;
      border: 2px solid;
      border-color: #e4e4e4;
    }
    &-spinner {
      animation: loading 1s linear infinite;
      background: transparent
        url(data:image/svg+xml;base64,)
        no-repeat;
      background-size: 100%;
    }
    &-left {
      margin-left: #{topx(8)};
    }
    &-top {
      margin-top: #{topx(8)};
    }
  }
}
@keyframes loading {
  from {
    transform: rotate(0);
  }
  to {
    transform: rotate(1turn);
  }
}
</style>